for k, v in pairs(VehiclesData) do
    table.insert(Vehicles, {
        spawncode = v.model,
        name = v.name,
        brand = v.brand,
        model = v.model,
        price = v.price,
        category = v.category,
        categoryLabel = Lang:t("categories." .. v.category),
        hash = joaat(v.model),
        type = v.type,
        shop = v.shop
    })
    Vehicles2[v.model] = {
        spawncode = v.model,
        name = v.name,
        brand = v.brand,
        model = v.model,
        price = v.price,
        category = v.category,
        categoryLabel = Lang:t("categories." .. v.category),
        hash = joaat(v.model),
        type = v.type,
        shop = v.shop
    }
    if not Categories[v.category] then
        Categories[v.category] = {
            name = v.category
        }
    end
